<script>
import CommonApi from "@/api/commonRequest";

export default {
  name: "subReplyList",
  data(){
    return {

    }
  },
  props : {
    subReply : null,
    index : null,
    subIndex : null
  },
  methods : {
    clickSubReply(uname,uid,previousCommentId,index){
      this.$parent.showReplyBox(uname,uid,previousCommentId,index)
    },
    action(commentId, action) {
      var like = 0
      var dislike = 0
      var _action = 0
      debugger
      //点赞数和点赞状态变换
      if (this.subReply.action == action) {
        if (action == 1)
          like = 1
        else
          dislike = 1
        _action = 0
      } else {
        if (action > 0) {
          like = 1;
          if (this.subReply.action < 0)
            dislike = -1
        } else if (action < 0) {
          dislike = 1
          if (this.subReply.action > 0)
            like = -1;
        }
        _action = action
      }

      this.$bus.$emit('subActionBack',this.index,this.subIndex,_action,like,dislike)

      var param = new FormData()
      param.append("commentId", commentId)
      param.append("action", action)
      CommonApi.formDataPostApi("/api/student/like/commentAction", param).then(res => {
        console.log("action>>success")
      })
    }
  }
}
</script>

